//js6-4
var dinnerHungry = 3;//1~10
function eat(food) {
    console.log("我想要吃" + food);
}
if (dinnerHungry < 3){// if(true)的話就會繼續往裡面讀取
    eat("pizza");
} else if (dinnerHungry == 7) {
    eat("水果");
} else if (dinnerHungry <= 6 && dinnerHungry >=3){
    eat("水餃");
}else{
    console.log("不餓！");
}

//js6-5 //**// switch(){}適合用來套入當你有確認的內容時(需要使用 && || 比較適合用 if else)
var light = "red";
function action(reply) {
    console.log("目前燈號是：" + reply + "燈")
};
switch (light) {
    case "red":
        action("紅");
        break;
    case "orange":
        action("橘");
        break;
    case "green":
        action("綠");
        break;

    default:
        console.log("號誌燈有問題");
        break;
}

